repeat_offense VARCHAR(50),
has_mandatory_minimum BOOLEAN,
minimum_sentence_days INTEGER,
maximum_sentence_years DECIMAL(4,2),

-- Arrest policies
mandatory_arrest_policy BOOLEAN,
dominant_aggressor_rule BOOLEAN,
