CREATE TABLE shelter (
shelter_id SERIAL,
shelter_name VARCHAR,
city VARCHAR,
state CHAR(2),
zip VARCHAR,
CONSTRAINT PK_shelter PRIMARY KEY (shelter_id),
CONSTRAINT FK_shelter_state FOREIGN KEY (state) REFERENCES state_laws(state_code)
);

CREATE TABLE charity (
